mapper.select:
SELECT COUNT(*)
FROM MEMBER
WHERE MEM_ID = #{mem_id}

SELECT COUNT(*)
FROM MEMBER
WHERE MEM_NAME  = #{mem_name}
  AND MEM_EMAIL = #{mem_email}

SELECT COUNT(*)
FROM MEMBER
WHERE MEM_ID = #{mem_id}
  AND MEM_EMAIL = #{mem_email}

mapper.update:
UPDATE MEMBER
   SET MEM_PASS = #{mem_pass}
 WHERE MEM_ID = #{mem_id}
